import React, { useState, useEffect } from "react";
import { getWallet, depositFunds, withdrawFunds } from "../utils/walletApi";

const WalletPanel = () => {
  const [wallet, setWallet] = useState(null);
  const [depositAmount, setDepositAmount] = useState("");
  const [withdrawAmount, setWithdrawAmount] = useState("");

  useEffect(() => {
    const fetchWallet = async () => {
      try {
        const data = await getWallet();
        setWallet(data);
      } catch (err) {
        console.error(
          "Wallet fetch error:",
          err.response?.data?.error || err.message
        );
      }
    };

    fetchWallet();
  }, []);

  const handleDeposit = async () => {
    const amount = parseFloat(depositAmount);
    if (!amount || amount <= 0) return alert("Enter a valid amount");

    try {
      await depositFunds(amount);
      alert(`₹${amount} deposited successfully`);
      setDepositAmount("");
      const updated = await getWallet();
      setWallet(updated);
    } catch (err) {
      alert(
        "Deposit failed: " + (err.response?.data?.error || "Network error")
      );
    }
  };

  const handleWithdraw = async () => {
    const amount = parseFloat(withdrawAmount);
    if (!amount || amount <= 0) return alert("Enter a valid amount");

    try {
      await withdrawFunds(amount);
      alert(`₹${amount} withdrawn successfully`);
      setWithdrawAmount("");
      const updated = await getWallet();
      setWallet(updated);
    } catch (err) {
      alert(
        "Withdraw failed: " + (err.response?.data?.error || "Network error")
      );
    }
  };

  return (
    <div className="wallet-panel">
      <h2>Wallet Overview</h2>
      {wallet ? (
        <>
          <p>
            <strong>Balance:</strong> ₹{wallet.balance}
          </p>
          <div>
            <input
              type="number"
              value={depositAmount}
              onChange={(e) => setDepositAmount(e.target.value)}
              placeholder="Deposit amount"
            />
            <button onClick={handleDeposit}>Deposit</button>
          </div>
          <div>
            <input
              type="number"
              value={withdrawAmount}
              onChange={(e) => setWithdrawAmount(e.target.value)}
              placeholder="Withdraw amount"
            />
            <button onClick={handleWithdraw}>Withdraw</button>
          </div>
        </>
      ) : (
        <p>Loading wallet...</p>
      )}
    </div>
  );
};

export default WalletPanel;
