import React, { useState } from "react";
import sign from "../assets/images/regis.png";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const BASE_URL = "http://localhost:4000/api/user";
const Signup = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    PhoneNumber: "",
    password: "",
    confirmPassword: "",
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log("Form submitted:", formData);

  try {
    const response = await axios.post(`${BASE_URL}/register`, {
      name: formData.name,
      email: formData.email,
      PhoneNumber: formData.PhoneNumber,
      password: formData.password,
    });
    const userId = response.data.user_id;
    console.log("User ID:", userId);

    console.log("Signup successful:", response.data);

    
    alert("Account created successfully!");
    navigate("/login"); 
    
  } catch (error) {
    console.error("Signup failed:", error.response?.data || error.message);
    alert("Signup failed. Please try again.");
  }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100 px-4">
      <div className="bg-white shadow-lg rounded-lg flex flex-col md:flex-row w-full max-w-4xl overflow-hidden">
        {/* Image Section */}
        <div className="md:w-1/2">
          <img
            src={sign}
            alt="Signup Visual"
            className="h-40% w-40% object-cover"
          />
           <h4 className="text-1xl mt-3  mb-6 text-gray-500 text-center">XPO is a leading online trading platform, providing users with access to global financial markets. 
              With intuitive features and expert support, XPO empowers traders to achieve their investment goals.</h4>
        </div>
        

        {/* Form Section */}
        <div className="md:w-1/2 p-8">
          <h2 className="text-2xl font-bold mb-4 text-indigo-600 text-center">Create an Account</h2>
          <form onSubmit={handleSubmit} className="space-y-7">
            <input
              type="text"
              name="name"
              placeholder="Full Name"
              value={formData.name}
              onChange={handleChange}
              className="w-full px-4 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-400"
              required
            />
            <input
              type="email"
              name="email"
              placeholder="Email Address"
              value={formData.email}
              onChange={handleChange}
              className="w-full px-4 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-400"
              required
            />
                <input
              type="number"
              name="PhoneNumber"
              placeholder="Phone Number"
              value={formData.PhoneNumber}
              onChange={handleChange}
              className="w-full px-4 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-400"
              required
            />

              <input
              type="password"
              name="password"
              placeholder="Password"
              value={formData.password}
              onChange={handleChange}
              className="w-full px-4 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-400"
              required
            />
            <button
              type="submit"
              className="w-full bg-indigo-600 text-white py-2 rounded-md hover:bg-indigo-700 transition duration-200"
            >
              Sign Up
            </button>
          </form>
          <p className="mt-4 text-sm text-center text-gray-600">
            Already have an account?{" "}
            <a href="/login" className="text-indigo-500 hover:underline">
              Log in
            </a>
          </p>
        </div>
      </div>
    </div>
  );
};

export default Signup;
