// export default Header


import React, { useEffect, useState } from "react";

const Header = () => {
  const [user, setUser] = useState(null);
  const [showDropdown, setShowDropdown] = useState(false);

  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      setUser(JSON.parse(storedUser));
    }
  }, []);

  const toggleDropdown = () => {
    setShowDropdown(!showDropdown);
  };

  return (
    <div>
      <div className="p-6">
        <h1 className="text-2xl font-bold">Welcome to the Home Page</h1>
        {user && (
          <div className="mt-4">
            <p><strong>Name:</strong> {user.name}</p>
            <p><strong>Email:</strong> {user.email}</p>
          </div>
        )}
      </div>

      <div className="header-area" id="sticky-header">
        <div className="container-fluid">
          <div className="row align-items-center d-flex fixed top-0 left-0 w-full z-50" style={{ backgroundColor: "#0b0b47" }}>
            <div className="col-lg-3 lsticky">
              <div className="header-logo wset">
                <a className="main-logo" href="https://xpo.ru/">
                  <img src="https://xpo.ru/assets/images/logo-white.png" alt="logo" />
                </a>
              </div>
            </div>

            <div className="col-lg-8 text-left">
              <nav className="cryptozen_menu">
                <div className="header-menu flex items-center justify-between">
                  <ul className="nav_scroll flex gap-6">
                    <li><a href="#whatxpo">what is xpo</a></li>
                    <li><a href="#whyxpo">why xpo</a></li>
                    <li><a href="#xpoindex">xpo indexes</a></li>
                    <li><a href="#fromindex">from index managers</a></li>
                    <li><a href="/blogPage">blog</a></li>
                    <li><a href="#team">team</a></li>
                    <li><a href="#contact">contact</a></li>
                  </ul>

                  {!user ? (
                    <div className="flex gap-4  font-serif  ">
                      
                      <a className="header-btn" href="/signup">Sign up</a>
                      <a className="header-btn" href="/login">Sign in</a>
                    </div>
                  ) : (
                    <div className="relative">
                      <button
                        onClick={toggleDropdown}
                        className="text-white font-semibold px-4 py-2 border border-white rounded hover:bg-white hover:text-black transition"
                      >
                        {user.name} ▼
                      </button>

                      {showDropdown && (
                        <div className="absolute right-0 mt-2 w-64 bg-white text-black rounded shadow-lg z-50 p-4">
                          <p className="font-semibold mb-2">Wallet Overview</p>
                          <div className="text-sm space-y-1">
                            <p><strong>Investment:</strong> ₹12,500</p>
                            <p><strong>Profile Balance:</strong> ₹3,200</p>
                            <p><strong>Wallet:</strong> ₹9,300</p>
                          </div>
                          <div className="mt-4 flex justify-between">
                            <button className="bg-green-600 text-white px-3 py-1 rounded hover:bg-green-700">Deposit</button>
                            <button className="bg-red-600 text-white px-3 py-1 rounded hover:bg-red-700">Withdraw</button>
                          </div>
                        </div>
                      )}
                    </div>
                  )}
                </div>
              </nav>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Header;
