import React, { useState, useEffect } from "react";
import { useAuthContext } from "../hooks/useAuthContext";
import { useLogout } from "../hooks/userLogout";
import { getWallet, depositFunds, withdrawFunds } from "../utils/walletApi";
const Header = () => {
  const { user } = useAuthContext();
  const { logout } = useLogout();
  const [showDropdown, setShowDropdown] = useState(false);
  const [showMobileMenu, setShowMobileMenu] = useState(false);
  const [isDepositing, setIsDepositing] = useState(false);
  const toggleDropdown = () => setShowDropdown(!showDropdown);
  const toggleMobileMenu = () => setShowMobileMenu(!showMobileMenu);
  const [showDepositModal, setShowDepositModal] = useState(false);
  const [depositAmount, setDepositAmount] = useState("");
  const [walletData, setWalletData] = useState(null);
  // useEffect(() => {
  //   const fetchWallet = async () => {
  //     try {
  //       const data = await getWallet();
  //       setWalletData(data);
  //     } catch (err) {
  //       console.error(
  //         "Wallet fetch error:",
  //         err.response?.data?.error || err.message
  //       );
  //     }
  //   };

  //   if (user) fetchWallet();
  // }, [user]);

  // Updated handleDeposit function
  // const handleDeposit = async () => {
  //   const amount = parseFloat(depositAmount);
  //   const regex = /^\d+(\.\d+)?$/; // Allow positive numbers and decimals

  //   if (!regex.test(depositAmount)) {
  //     return alert("Enter a valid numeric amount (e.g., 100 or 100.50)");
  //   }

  //   if (amount <= 0) {
  //     return alert("Amount must be greater than 0");
  //   }

  //   setIsDepositing(true);

  //   try {
  //     await depositFunds(amount);
  //     alert(`₹${amount} deposited successfully`);
  //     setShowDepositModal(false);
  //     setDepositAmount("");
  //     const updated = await getWallet();
  //     setWalletData(updated);
  //   } catch (err) {
  //     console.error("Deposit error:", err.response?.data?.error || err.message);
  //     alert(
  //       "Deposit failed: " + (err.response?.data?.error || "Network error")
  //     );
  //   } finally {
  //     setIsDepositing(false);
  //   }
  // };
  // const handleDeposit = async () => {
  //   const amount = parseFloat(depositAmount);
  //   if (!amount || amount <= 0) return alert("Enter a valid amount");

  //   try {
  //     await depositFunds(amount);
  //     alert(`₹${amount} deposited successfully`);
  //     setShowDepositModal(false);
  //     setDepositAmount("");
  //     const updated = await getWallet();
  //     setWalletData(updated);
  //   } catch (err) {
  //     console.error("Deposit error:", err.response?.data?.error || err.message);
  //     alert(
  //       "Deposit failed: " + (err.response?.data?.error || "Network error")
  //     );
  //   }
  // };
