import React, { useState } from "react";
import login from "../assets/images/login.png";
import axios from "axios";

const BASE_URL = "http://localhost:4000/api/user";
const Login = () => {
  const [formData, setFormData] = useState({
    email: "",
    password: "",
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log("Login submitted:", formData);
   
    
  try {
    const response = await axios.post(`${BASE_URL}/login`, {
      email: formData.email,
      password: formData.password,
    });
    const userId = response.data.user_id;
    console.log("User ID:", userId);

    // Handle success
    console.log("Login successful:", response.data);
    alert("login successfully!");

    // Example: Save token or user info
    localStorage.setItem("token", response.data.token);
   
    // Redirect or show success message
    // navigate("/dashboard"); // if using React Router
  } catch (error) {
    // Handle error
    console.error("Login failed:", error.response?.data || error.message);
    alert("Login failed. Please check your credentials.");
  }

    
  };



  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100 px-4">
      <div className="bg-white shadow-lg rounded-lg flex flex-col md:flex-row w-full max-w-4xl overflow-hidden">
        {/* Image Section */}
        <div className="md:w-1/2">
          <img
            src={login}
            alt="Login Visual"
            className="h-full w-full object-cover"
          />
          
        </div>
        {/* Form Section */}
        <div className="md:w-1/2 p-8">
          <h2 className="text-2xl font-bold mb-6 text-indigo-600 text-center">Welcome Back</h2>
          <h4 className="text-2xl font-bold mb-6 text-indigo-600 text-center">XPO is a leading online trading platform, providing users with access to global financial markets. With intuitive features and expert support, XPO empowers traders to achieve their investment goals.</h4>
          <form onSubmit={handleSubmit} className="space-y-4">
            <input
              type="email"
              name="email"
              placeholder="Email Address"
              value={formData.email}
              onChange={handleChange}
              className="w-full px-4 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-400"
              required
            />
            <input
              type="password"
              name="password"
              placeholder="Password"
              value={formData.password}
              onChange={handleChange}
              className="w-full px-4 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-400"
              required
            />
            <button
              type="submit"
              className="w-full bg-indigo-600 text-white py-2 rounded-md hover:bg-indigo-700 transition duration-200"
            >
              Sign In
            </button>
          </form>
          <p className="mt-4 text-sm text-center text-gray-600">
            Don't have an account?{" "}
            <a href="/signup" className="text-indigo-500 hover:underline">
              Sign up
            </a>
          </p>
        </div>
      </div>
    </div>
  );
};

export default Login;
