import React, { useState } from "react";
import { useAuthContext } from "../hooks/useAuthContext"; // adjust path if needed
import { useLogout } from "../hooks/userLogout"; // optional for logout button

const Header = () => {
  const { user } = useAuthContext();
  const { logout } = useLogout();
  const [showDropdown, setShowDropdown] = useState(false);

  const toggleDropdown = () => setShowDropdown(!showDropdown);

  // Dummy wallet data — replace with dynamic values if available
  const walletData = {
    investment: "₹12,500",
    profile: "₹3,200",
    wallet: "₹9,300",
  };

  return (
    <div className="header-area" id="sticky-header">
      <div className="container-fluid">
        <div className="row align-items-center d-flex fixed top-0 left-0 w-full z-50" style={{ backgroundColor: "#0b0b47" }}>
          <div className="col-lg-3 lsticky">
            <div className="header-logo wset">
              <a className="main-logo" href="https://xpo.ru/">
                <img src="https://xpo.ru/assets/images/logo-white.png" alt="logo" />
              </a>
              <a className="stiky-logo" href="https://xpo.ru/">
                <img src="https://xpo.ru/assets/images/logo-white.png" alt="logo" />
              </a>
            </div>
          </div>

          <div className="col-lg-8 text-left">
            <nav className="cryptozen_menu">
              <div className="header-menu flex items-center justify-between">
                <ul className="nav_scroll flex gap-6">
                  <li><a href="#whatxpo">what is xpo</a></li>
                  <li><a href="#whyxpo">why xpo</a></li>
                  <li><a href="#xpoindex">xpo indexes</a></li>
                  <li><a href="#fromindex">from index managers</a></li>
                  <li><a href="/blogPage">blog</a></li>
                  <li><a href="#team">team</a></li>
                  <li><a href="#contact">contact</a></li>
                </ul>

                {!user ? (
                  <div className="flex gap-4">
                    <a className="header-btn text-white" href="/signup">Sign up</a>
                    <a className="header-btn text-white" href="/login">Sign in</a>
                  </div>
                ) : (
                  <div className="relative">
                    <button
                      onClick={toggleDropdown}
                      className="text-white font-semibold px-4 py-2 border border-white rounded hover:bg-white hover:text-black transition"
                    >
                      {user.name} ▼
                    </button>

                    {showDropdown && (
                      <div className="absolute right-0 mt-2 w-72 bg-white text-black rounded shadow-lg z-50 p-4">
                        <p className="font-semibold mb-2">Wallet Overview</p>
                        <div className="text-sm space-y-1">
                          <p><strong>Investment:</strong> {walletData.investment}</p>
                          <p><strong>Profile Balance:</strong> {walletData.profile}</p>
                          <p><strong>Wallet:</strong> {walletData.wallet}</p>
                        </div>
                        <div className="mt-4 flex justify-between">
                          <button className="bg-green-600 text-white px-3 py-1 rounded hover:bg-green-700">Deposit</button>
                          <button className="bg-red-600 text-white px-3 py-1 rounded hover:bg-red-700">Withdraw</button>
                        </div>
                        <div className="mt-4 text-right">
                          <button onClick={logout} className="text-sm text-indigo-600 hover:underline">Logout</button>
                        </div>
                      </div>
                    )}
                  </div>
                )}
              </div>
            </nav>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Header;
