import axiosInstance from "./axiosInstance";

import axios from "axios";
export const getWallet = async () => {
  const res = await axiosInstance.get("/wallet");
  return res.data;
};

// export const depositFunds = async (amount) => {
//   const res = await axiosInstance.post("/wallet/deposit", { amount });
//   return res.data;
// };

export const depositFunds = async (amount) => {
  const user = JSON.parse(localStorage.getItem("user")); // Or get from auth context
  const res = await axios.post("/api/wallet/deposit", {
    userId: user._id, // Ensure this matches your auth context
    amount,
  });
  return res.data;
};
